const Userinput = () => {
    const [name, setName] = useState("Stephanie");
    return(
        <div>
            <h1>Hello, {name}!</h1>
            <form>
            <label for="name">Update Name: </label>
            <input type="text" name="name"/>
            <button>Save</button>
            </form>

        </div>
    )
};
export default Userinput;
